INSERT INTO xcart_languages VALUES ('en', 'opt_descr_xpc_private_key', 'Private key is used to decrypt responses and callback requests that the store receives from your X-Payments installation. This value should be obtained from this online store details page in X-Payments back-end.', 'Options');
