INSERT INTO xcart_languages VALUES ('en', 'opt_descr_use_f_sendmail_option', 'Servers with certain configurations may block e-mail messages without this parameter.<br />The Web server user should be added as a trusted user to the sendmail configuration to prevent adding the \'X-Warning\' header to the message when the envelope sender (-f) is set using this method.<br />For sendmail users, this file is /etc/mail/trusted-users.<br />Find out more at <a href=\"http://php.net/mail\" target=\"_blank\">http://php.net/mail</a>', 'Options');
INSERT INTO xcart_languages VALUES ('en', 'opt_descr_use_old_products_lng', 'If your custom application or module uses SQL queries SELECT for fields product, descr, fulldescr and keyword in tables xcart_products and/or xcart_products_lng, you can enable this mode and thus not have to modify your module.<br />This option can slow down your shop pages in some cases.', 'Options');
