INSERT INTO xcart_languages VALUES ('en', 'txt_cc_2conew_note', '<b>Important</b>: Follow the steps below to enable X-Cart to track your 2Checkout orders.<br /><br />(If you use the Classic Sellers Area)<ol><li>Log in to your 2Checkout account.</li><li>Click on the <i>\"Look &amp; Feel settings\"</i> in the <i>\"Helpful links\"</i> section.</li><li>Set the option <i>\"Direct Return?\"</i> to \"Yes\".</li><li>Set the <i>\"Approved URL\"</i> to<br /><b>{{http_location}}/payment/cc_2conew.php</b></li><li>Enter value of the field <i>\"Your secret Word\"</i>. A secret word is a combination of letters and numbers known only to you and 2CheckOut.</li></ol>(If you use the new Vendor Admin Area)<ol><li>Log in to your 2Checkout account</li><li>Go to the <i>Account</i> -> <i>Site Management page</i></li><li>Set the option <i>\"Direct Return\"</i> to <i>\"Immediately return to my website\"</i></li><li>Set the \"Approved URL\" to<br /><b>{{http_location}}/payment/cc_2conew.php</b></li><li>In the <i>\"Direct Return\"</i> section, enter the value of the field <i>\"Secret Word\"</i>. A secret word is a combination of letters and numbers known only to you and 2CheckOut.</li><li>Follow the URL <b>{{http_location}}/admin/cc_processing.php?mode=update&amp;cc_processor=cc_2conew.php</b></li><li>Enter your secret word in X-Cart.</li></ol>', 'Text');
