INSERT INTO xcart_languages VALUES ('en', 'txt_enter_merchant_password_note', 'Entering your current Merchant key into this field allows you 1) to access order details encrypted with Merchant key-based Blowfish encryption; 2) to apply Merchant key-based encryption to new orders which, so far, have been encrypted with the hard-coded Blowfish key (the one stored in the $blowfish_key variable of the config.php file).<br /><br />\nNote: As your Merchant key is not stored in the system, new orders which are placed by your customers are not encrypted with this key right away. They are first encrypted with the hard-coded Blowfish key stored in the config.php file and are re-encrypted using the Merchant key only when you enter your Merchant key into the system.', 'Text');
