INSERT INTO xcart_languages VALUES ('en', 'txt_fedex_help_charges_type', '<strong>{{currency_code}}</strong> This value is used if you want to add a fixed handling charge to all shipments.<br /><br /><strong>% of base</strong> This value is used if you want to have a variable handling charge calculated as a percentage of the net freight charge, excluding any other surcharges levied on the shipment.<br /><br /><strong>% of net</strong> This value is used if you want to have a variable handling charge calculated on the entire net charge of the shipment, including all surcharges.<br /><br /><strong>% of net (excluding taxes)</strong> This value is used if you are a Canada-origin shipper and want to exclude Canadian taxes from the variable handling charge net percentage.', 'Text');
INSERT INTO xcart_languages VALUES ('en', 'txt_fedex_limits_note', 'The settings \"Maximum package weight\" and \"Maximum package dimensions\" define the maximum weight and size of the shipping package that you do not wish to be exceeded when products are shipped to customers. If the total weight and/or volume of the products ordered by a customer exceed the limitations defined by these settings, and the checkbox \"Split the shipment into multiple packages\" is selected, the shipment is split into several packages. <br />\nPlease note that the maximum package weight and maximum package dimensions that you set on this page must not exceed the actual limitations imposed by FedEx service. <br />\nIf you set the maximum package weight or any of the maximum package dimension values to zero, X-Cart will use the default limitations established by FedEx.', 'Text');
