INSERT INTO xcart_languages VALUES ('en', 'txt_frf_limit_for_preauth_opt_note', 'Define the maximum allowed value of the fraud risk factor. When exceeded, X-Cart will force to perform the transaction in the \"Auth only\" mode even if the payment module is setup to use a different mode.<br />\n<br />\nThe \"Auth only\" mode means that the system will only freeze the order total but not withdraw it until the store administrator decides to capture the money manually.<br />\n<br />\nIf the payment method you use does not allow for authorize-only transactions, the transaction will not perform and the order will be placed with the Queued status.', 'Text');
INSERT INTO xcart_languages VALUES ('en', 'txt_froogle_format_note', 'Froogle/GoogleBase has some restrictions as to the format of tab-delimited bulk upload (a quote from <a href=\"http://www.google.com/support/merchants/bin/answer.py?answer=160588\" target=\"_blank\">http://www.google.com/support/merchants/bin/answer.py?answer=160588</a>):<br /><ul><li>The file must be tab-delimited plain text.</li><li>The first line of the file is the header -- it must contain attribute names, separated by tabs.</li><li>One item per line; separate each attribute in that line by a tab.</li><li>Do not include trailing tabs at the end of lines.</li><li>Do not include tabs or line breaks in the attributes themselves.</li></ul>', 'Text');
