INSERT INTO xcart_languages VALUES ('en', 'txt_html_tags_in_description', '<b>* Note:</b> If no HTML tags are detected in the product description text on form submit, this text will be treated as plain text and converted to HTML before saving, so any newline characters used in this text will be replaced by the &lt;br /&gt; tag to ensure that line breaks are displayed correctly.', 'Text');
INSERT INTO xcart_languages VALUES ('en', 'txt_https_check_warning', '<font color=\"red\">Warning! HTTPS/SSL check failed. Please make sure that HTTPS is configured properly.</font><br />Please, disable <br /><i>\'Use HTTPS for users login and registration\'</i><br /><i>\'Use secure login form on a separate page (HTTPS)\'</i> settings.<br />And enable <br /><i>\'Redirect customers from HTTPS to HTTP\'</i><br /> to ensure the proper operation of the store. (You can restore the settings when HTTPS/SSL works properly.)<br />Current HTTPS location of your store is: {{https_location}}.<br />Click here to learn how to configure HTTPS in X-Cart.', 'Text');
INSERT INTO xcart_languages VALUES ('en', 'txt_https_check_warning_bouncer', '<font color=\"black\">Warning! HTTPS check failed because no HTTPS modules are installed on your server or none of the installed HTTPS modules is configured properly.</font><br />Make sure the desired module is installed on the server. Click <a href=\"configuration.php?option=General#anchor_httpsmod\">here</a> to select the HTTPS module.<br />Make sure functionality related to the three settings below runs properly on your server.', 'Text');
