INSERT INTO xcart_languages VALUES ('en', 'txt_shipping_conversion_rate', 'The shipping cost is always returned in the local currency of the country where your shop is located. If the prices in your shop are in non-local currency (for example the shop is located in Great Britain but prices are in USD) then you need to define currency conversion rate to convert shipping cost returned by UPS in necessary currency.', 'Text');
