INSERT INTO xcart_languages VALUES ('zh', 'txt_html_catalog_note_clean_ulr_disable', 'If your store has many products and categories, the generation of an HTML catalog may take considerable time. That is why, if you need SEO-friendly URLs, we recommend using <a href=\"{{href}}\">X-Cart\'s Clean URLs</a> functionality.', 'Text');
