INSERT INTO xcart_languages VALUES ('zh', 'txt_gcheckout_charge_order_note', 'This command instructs Google Checkout to charge the buyer for the order. After the order reaches the CHARGEABLE order state, you have 72 hours to capture funds by issuing the \'Charge order\' command.', 'Text');
