INSERT INTO xcart_languages VALUES ('zh', 'opt_descr_use_old_products_lng', 'If your custom application or module uses SQL queries SELECT for fields product, descr, fulldescr and keyword in tables xcart_products and/or xcart_products_lng, you can enable this mode and thus not have to modify your module.<br />This option can slow down your shop pages in some cases.', 'Options');
