INSERT INTO xcart_languages VALUES ('zh', 'opt_descr_partner_cookie_length', 'Enter a number of days during which partner cookies must be kept on a buyer\'s local computer. This ensures that a partner will get a commission even if the referred buyer makes a purchase not at the first visit, but a few days later while the cookies are still kept on the buyer\'s local computer.<br />\n<br />\nIf you do not wish to save partner cookies at local computers of your store visitors, leave the value of the field empty or equal to 0.', 'Options');
