INSERT INTO xcart_languages VALUES ('zh', 'opt_descr_company_mail_from', 'Some spam filters may block an email message if the domain part of the address in the From header does not correspond to the domain that the message was sent from. The email address that you specify here will be automatically put into the From header of all email messages you send from the store. It allows you not to affect the address in the Reply-To header and ensures that your outgoing email messages will not be blocked because of mismatching domains.<br /><br />Example: If your store domain is www.store.com but the default email address has a different domain part, specify the address info@store.com and all the outgoing messages will have it in the From header.', 'Options');
